document.addEventListener('DOMContentLoaded', function () {

  var modalElements = Array.from(document.querySelectorAll('[data-modal]'));

  modalElements.map(function (el) {
    var modalId = el.dataset.modal;
    var modal = document.querySelector(modalId);
    var modalClose = modal.querySelector('.js-modal-close');

    el.addEventListener('click', function () {
      showOverlay();
      modal.classList.add('modalIsShow');
      modalClose.addEventListener('click', function () {
        modal.classList.remove('modalIsShow');
        setTimeout(function () {
          hideOverlay();
        }, 300);
      });
    });
  });
});

